import React, { useState } from "react";
import axios from "axios";

const API_URL = "https://opv17qccr5.execute-api.us-east-1.amazonaws.com/dev/invoke-agent"; // Replace with your API Gateway URL

function App() {
    const [prompt, setPrompt] = useState("");
    const [messages, setMessages] = useState([]); // Store chat messages
    const [loading, setLoading] = useState(false);

    const handleSend = async () => {
        if (!prompt.trim()) return;

        const userMessage = { text: prompt, sender: "user" };
        setMessages((prev) => [...prev, userMessage]); // Add user message to chat
        setPrompt("");
        setLoading(true);

        try {
            const res = await axios.post(API_URL, { prompt }, {
                headers: { "Content-Type": "application/json" }
            });

            const botMessage = { text: res.data.response, sender: "bot" };
            setMessages((prev) => [...prev, botMessage]); // Add bot response to chat
        } catch (error) {
            console.error("Error calling API:", error);
            const errorMessage = { text: "Sorry, something went wrong. Reporting to Shusei.", sender: "bot" };
            setMessages((prev) => [...prev, errorMessage]);
        }

        setLoading(false);
    };

    const handleKeyDown = (e) => {
        if (e.key === "Enter" && !e.shiftKey) {
            e.preventDefault();
            handleSend();
        }
    };

    return (
        <div style={styles.container}>
            <h2>Ask-me</h2>
            <div style={styles.chatWindow}>
                {messages.map((msg, index) => (
                    <div key={index} style={msg.sender === "user" ? styles.userBubble : styles.botBubble}>
                        {msg.text}
                    </div>
                ))}
            </div>
            <div style={styles.chatbox}>
                <textarea
                    style={styles.input}
                    placeholder="Ask me anything about Shusei Yokoi!"
                    value={prompt}
                    onChange={(e) => setPrompt(e.target.value)}
                    onKeyDown={handleKeyDown}
                />
                <button style={styles.button} onClick={handleSend} disabled={loading}>
                    {loading ? "Thinking..." : "Send"}
                </button>
            </div>
        </div>
    );
}

const styles = {
    container: { textAlign: "center", padding: "20px", fontFamily: "Arial", maxWidth: "600px", margin: "auto" },
    chatWindow: {
        display: "flex",
        flexDirection: "column",
        alignItems: "flex-start",
        border: "1px solid #ccc",
        padding: "10px",
        height: "400px",
        overflowY: "auto",
        background: "#f9f9f9",
        borderRadius: "10px",
        marginBottom: "10px"
    },
    userBubble: {
        alignSelf: "flex-end",
        backgroundColor: "#007bff",
        color: "white",
        padding: "10px",
        borderRadius: "10px",
        marginBottom: "5px",
        maxWidth: "75%",
        textAlign: "right"
    },
    botBubble: {
        alignSelf: "flex-start",
        backgroundColor: "#e5e5ea",
        color: "black",
        padding: "10px",
        borderRadius: "10px",
        marginBottom: "5px",
        maxWidth: "75%",
        textAlign: "left"
    },
    chatbox: { display: "flex", flexDirection: "column", alignItems: "center" },
    input: { width: "80%", height: "50px", fontSize: "16px", padding: "10px", marginBottom: "10px", resize: "none" },
    button: { padding: "10px 20px", fontSize: "16px", cursor: "pointer", backgroundColor: "#007bff", color: "white", border: "none", borderRadius: "5px" }
};

export default App;
