import React, { useState } from "react";
import axios from "axios";

const API_URL = "https://opv17qccr5.execute-api.us-east-1.amazonaws.com/dev"; // Replace with your API Gateway URL

function App() {
    const [prompt, setPrompt] = useState("");
    const [response, setResponse] = useState("");
    const [loading, setLoading] = useState(false);

    const handleSend = async () => {
        if (!prompt.trim()) return;

        setLoading(true);
        setResponse("Thinking...");

        try {
            const res = await axios.post(API_URL, { prompt }, {
                headers: { "Content-Type": "application/json" }
            });

            setResponse(res.data.response);
        } catch (error) {
            console.error("Error calling API:", error);
            setResponse("Sorry, something went wrong.");
        }

        setLoading(false);
    };

    return (
        <div style={styles.container}>
            <h2>Chatbot</h2>
            <div style={styles.chatbox}>
                <textarea 
                    style={styles.input}
                    placeholder="Ask me anything..."
                    value={prompt}
                    onChange={(e) => setPrompt(e.target.value)}
                />
                <button style={styles.button} onClick={handleSend} disabled={loading}>
                    {loading ? "Thinking..." : "Send"}
                </button>
            </div>
            <div style={styles.response}>
                <strong>Response:</strong> <p>{response}</p>
            </div>
        </div>
    );
}

const styles = {
    container: { textAlign: "center", padding: "50px", fontFamily: "Arial" },
    chatbox: { display: "flex", flexDirection: "column", alignItems: "center" },
    input: { width: "80%", height: "50px", fontSize: "16px", padding: "10px", marginBottom: "10px" },
    button: { padding: "10px 20px", fontSize: "16px", cursor: "pointer", backgroundColor: "#007bff", color: "white", border: "none", borderRadius: "5px" },
    response: { marginTop: "20px", fontSize: "18px" }
};

export default App;
