import React, { useState, useEffect, useRef } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import axios from "axios";
import ReactMarkdown from "react-markdown";
import Lottie from "lottie-react";
import loadingAnimation from "./assets/loading.json";
import about_me_pic from "./assets/about_me_pic.png";
import bot_icon from "./assets/bot_icon.jpg";
import TermsOfUse from "./TermsOfUse";
import PrivacyPolicy from "./PrivacyPolicy";

const API_URL = "https://opv17qccr5.execute-api.us-east-1.amazonaws.com/dev/invoke-agent";

const staticStyles = {
    container: { textAlign: "center", fontFamily: "Arial", margin: "auto", position: "relative" },
    chatWindow: { display: "flex", flexDirection: "column", alignItems: "flex-start", border: "1px solid #ccc", padding: "10px", overflowY: "auto", background: "#f9f9f9", borderRadius: "10px", marginBottom: "10px", height: "500px" },
    userBubbleContainer: { display: "flex", justifyContent: "flex-end", width: "100%", marginBottom: "5px" },
    botBubbleContainer: { display: "flex", justifyContent: "flex-start", alignItems: "center", width: "100%", marginBottom: "5px" },
    messageBubble: { padding: "10px", borderRadius: "10px", maxWidth: "75%", whiteSpace: "pre-wrap", wordWrap: "break-word", overflowWrap: "break-word" },
    userBubble: { backgroundColor: "#009DB4", color: "white", textAlign: "right" },
    botBubble: { backgroundColor: "#e5e5ea", color: "black", textAlign: "left" },
    botAvatar: { width: "40px", height: "40px", borderRadius: "50%", marginRight: "10px" },
    input: { fontSize: "16px", padding: "10px", resize: "none", border: "2px solid #ccc", borderRadius: "5px", outline: "none", width: "80%", margin: "10px 0" },
    button: { fontSize: "16px", cursor: "pointer", backgroundColor: "#009DB4", color: "white", border: "none", borderRadius: "5px", padding: "10px 20px" },
    loadingAnimation: { width: 50, height: 50 }
};

function ChatApp() {
    const [prompt, setPrompt] = useState("");
    const [messages, setMessages] = useState([
        { 
            text: "**Hi there! Welcome to Ask Me Bot!**\n\nI'm an AI assistant created by Shusei Yokoi to help you learn more about his career and experience! You can ask me anything related to his professional background—like `Does he have leadership experience?` or `What kind of Data Science experience does he have?` Feel free to ask me!",
            sender: "bot" 
        }
    ]);
    const [loading, setLoading] = useState(false);
    const chatEndRef = useRef(null);

    const handleSend = async () => {
        if (!prompt.trim()) return;

        const userMessage = { text: prompt, sender: "user" };
        setMessages((prev) => [...prev, userMessage]);
        setPrompt("");
        setLoading(true);

        const thinkingMessage = { text: "Thinking...", sender: "bot", isThinking: true };
        setMessages((prev) => [...prev, thinkingMessage]);

        try {
            const res = await axios.post(API_URL, { prompt }, {
                headers: { "Content-Type": "application/json" }
            });

            setMessages((prev) => prev.filter(msg => !msg.isThinking));
            const botMessage = { text: res.data.response, sender: "bot" };
            setMessages((prev) => [...prev, botMessage]);
        } catch (error) {
            console.error("Error calling API:", error);
            setMessages((prev) => prev.filter(msg => !msg.isThinking));
            const errorMessage = { text: "Sorry, something went wrong. Reporting to Shusei.", sender: "bot" };
            setMessages((prev) => [...prev, errorMessage]);
        }

        setLoading(false);
    };

    useEffect(() => {
        chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    return (
        <div style={staticStyles.container}>
            <h2>Ask Me</h2>
            <div style={staticStyles.chatWindow}>
                {messages.map((msg, index) => (
                    <div key={index} style={msg.sender === "user" ? staticStyles.userBubbleContainer : staticStyles.botBubbleContainer}>
                        {msg.sender === "bot" && (
                            <img src={bot_icon} alt="Bot" style={staticStyles.botAvatar} />
                        )}
                        <div style={{ ...staticStyles.messageBubble, ...(msg.sender === "user" ? staticStyles.userBubble : staticStyles.botBubble) }}>
                            {msg.isThinking ? (
                                <Lottie animationData={loadingAnimation} style={staticStyles.loadingAnimation} />
                            ) : (
                                <ReactMarkdown>{msg.text}</ReactMarkdown>
                            )}
                        </div>
                    </div>
                ))}
                <div ref={chatEndRef} />
            </div>
            <div>
                <textarea
                    style={staticStyles.input}
                    placeholder="Ask me anything about Shusei Yokoi!"
                    value={prompt}
                    onChange={(e) => setPrompt(e.target.value)}
                    onKeyDown={(e) => e.key === "Enter" && !e.shiftKey && (e.preventDefault(), handleSend())}
                />
                <button style={staticStyles.button} onClick={handleSend} disabled={loading}>
                    {loading ? "Thinking..." : "Send"}
                </button>
            </div>
        </div>
    );
}

function App() {
    return (
        <Router>
            <Routes>
                <Route path="/" element={<ChatApp />} />
                <Route path="/terms" element={<TermsOfUse />} />
                <Route path="/privacy" element={<PrivacyPolicy />} />
            </Routes>
        </Router>
    );
}

export default App;
