import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

const API_URL = "https://opv17qccr5.execute-api.us-east-1.amazonaws.com/dev/invoke-agent"; // Replace with your API Gateway URL

function App() {
    const [prompt, setPrompt] = useState("");
    const [messages, setMessages] = useState([
        { 
            text: "Hi there! Welcome to Ask-Me Bot! \n\n"
            + "I'm an AI assistant created by Shusei Yokoi to help you learn more about his career and experience!",

            sender: "bot" 
        }
    ]); // Stores chat history with first bot message
    const [loading, setLoading] = useState(false);
    const [isFocused, setIsFocused] = useState(false); // Track input focus
    const chatEndRef = useRef(null); // Ref for auto-scrolling

    const handleSend = async () => {
        if (!prompt.trim()) return;

        const userMessage = { text: prompt, sender: "user" };
        setMessages((prev) => [...prev, userMessage]); // Add user message to chat
        setPrompt("");
        setLoading(true);

        // Show "thinking..." message
        const thinkingMessage = { text: "Thinking...", sender: "bot", isThinking: true };
        setMessages((prev) => [...prev, thinkingMessage]);

        try {
            const res = await axios.post(API_URL, { prompt }, {
                headers: { "Content-Type": "application/json" }
            });

            // Remove "thinking..." and add the real bot response
            setMessages((prev) => prev.filter(msg => !msg.isThinking));
            const botMessage = { text: res.data.response, sender: "bot" };
            setMessages((prev) => [...prev, botMessage]);
        } catch (error) {
            console.error("Error calling API:", error);
            setMessages((prev) => prev.filter(msg => !msg.isThinking));
            const errorMessage = { text: "Sorry, something went wrong. Reporting to Shusei.", sender: "bot" };
            setMessages((prev) => [...prev, errorMessage]);
        }

        setLoading(false);
    };

    const handleKeyDown = (e) => {
        if (e.key === "Enter" && !e.shiftKey) {
            e.preventDefault();
            handleSend();
        }
    };

    // Auto-scroll to bottom whenever messages update
    useEffect(() => {
        chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    return (
        <div style={styles.container}>
            <a href="https://shuseiyokoi.notion.site/?pvs=4" target="_blank" rel="noopener noreferrer" style={styles.aboutMe}>
                About Me
                <img src="https://your-image-url.com/avatar.png" alt="About Me" style={styles.aboutMeIcon} />
            </a>
            <h2>Ask-Me</h2>
            <div style={styles.chatWindow}>
                {messages.map((msg, index) => (
                    <div key={index} style={msg.sender === "user" ? styles.userBubbleContainer : styles.botBubbleContainer}>
                        {msg.sender === "bot" && <img src="https://your-bot-avatar-url.com/avatar.png" alt="Bot" style={styles.botAvatar} />}
                        <div style={msg.sender === "user" ? styles.userBubble : styles.botBubble}>
                            {msg.text}
                        </div>
                    </div>
                ))}
                <div ref={chatEndRef} /> {/* Auto-scroll anchor */}
            </div>
            <div style={styles.chatbox}>
                <textarea
                    style={{ ...styles.input, ...(isFocused ? styles.inputFocused : {}) }}
                    placeholder="Ask me anything about Shusei Yokoi!"
                    value={prompt}
                    onChange={(e) => setPrompt(e.target.value)}
                    onKeyDown={handleKeyDown}
                    onFocus={() => setIsFocused(true)}
                    onBlur={() => setIsFocused(false)}
                />
                <button style={styles.button} onClick={handleSend} disabled={loading}>
                    {loading ? "Thinking..." : "Send"}
                </button>
            </div>
        </div>
    );
}

const styles = {
    container: { 
        textAlign: "center", 
        padding: "20px", 
        fontFamily: "Arial", 
        maxWidth: "600px", 
        margin: "auto",
        position: "relative"
    },
    aboutMe: {
        position: "absolute",
        top: "10px",
        left: "10px",
        textDecoration: "none",
        fontSize: "14px",
        color: "#007bff",
        fontWeight: "bold",
        display: "flex",
        alignItems: "center"
    },
    aboutMeIcon: {
        width: "24px",
        height: "24px",
        borderRadius: "50%",
        marginLeft: "8px"
    },
    chatWindow: {
        display: "flex",
        flexDirection: "column",
        alignItems: "flex-start",
        border: "1px solid #ccc",
        padding: "10px",
        height: "400px",
        overflowY: "auto",
        background: "#f9f9f9",
        borderRadius: "10px",
        marginBottom: "10px"
    },
    userBubbleContainer: {
        display: "flex",
        justifyContent: "flex-end",
        width: "100%",
        marginBottom: "5px"
    },
    botBubbleContainer: {
        display: "flex",
        justifyContent: "flex-start",
        alignItems: "center",
        width: "100%",
        marginBottom: "5px"
    },
    userBubble: {
        backgroundColor: "#007bff",
        color: "white",
        padding: "10px",
        borderRadius: "10px",
        maxWidth: "75%",
        textAlign: "right"
    },
    botBubble: {
        backgroundColor: "#e5e5ea",
        color: "black",
        padding: "10px",
        borderRadius: "10px",
        maxWidth: "75%",
        textAlign: "left"
    },
    botAvatar: {
        width: "30px",
        height: "30px",
        borderRadius: "50%",
        marginRight: "10px"
    },
    chatbox: { display: "flex", flexDirection: "column", alignItems: "center" },
    input: {
        width: "80%",
        height: "50px",
        fontSize: "16px",
        padding: "10px",
        marginBottom: "10px",
        resize: "none",
        border: "2px solid #ccc",  // Default border color
        borderRadius: "5px",
        outline: "none", // Remove default browser outline
        transition: "border 0.3s ease-in-out",
    },
    inputFocused: {
        border: "2px solid #009DB4", // Highlight color when selected
    },
    button: { 
        padding: "10px 20px", 
        fontSize: "16px", 
        cursor: "pointer", 
        backgroundColor: "#007bff", 
        color: "white", 
        border: "none", 
        borderRadius: "5px" 
    }
};

export default App;
